import React, { useEffect, useState } from 'react';
import '../../assets/style/SearchFlight.css';
import Plane from '../../assets/images/plane.png';
import FlightService from '../../services/FlightService';

const SearchFlight = ({ onSearch }) => {
  const [filter, setFilter] = useState({
    from: '',
    to: '',
    depart: '',
    arrival: ''
  });
  const [location, setLocation] = useState('');
  const [destination, setDestination] = useState('');
  const [fromDestinations, setFromDestination] = useState([]);
  const [toDestinations, setToDestination] = useState([]);
  const [filteredFromDestinations, setFilteredFromDestinations] = useState([]);
  const [showFromDropdown, setShowFromDropdown] = useState(false);
  const [filteredToDestinations, setFilteredToDestinations] = useState([]);
  const [showToDropdown, setShowToDropdown] = useState(false);

  useEffect(() => {
    const fetchFromDestinations = async () => {
      if (location) {
        try {
          const flightData = await FlightService.getDestinations({ search: location });
          setFromDestination(flightData);
        } catch (error) {
          console.error('Error fetching from destinations:', error);
        }
      }
    };

    fetchFromDestinations();
  }, [location]);

  useEffect(() => {
    const fetchToDestinations = async () => {
      if (destination) {
        try {
          const flightData = await FlightService.getDestinations({ search: destination });
          setToDestination(flightData);
        } catch (error) {
          console.error('Error fetching to destinations:', error);
        }
      }
    };

    fetchToDestinations();
  }, [destination]);

  useEffect(() => {
    if (location && fromDestinations.length) {
      setFilteredFromDestinations(fromDestinations);
      setShowFromDropdown(true);
    } else {
      setShowFromDropdown(false);
    }
  }, [location, fromDestinations]);

  useEffect(() => {
    if (destination && toDestinations.length) {
      setFilteredToDestinations(toDestinations);
      setShowToDropdown(true);
    } else {
      setShowToDropdown(false);
    }
  }, [destination, toDestinations]);

  const handleFromDestination = (selectedDestination) => {
    const displayText = `${selectedDestination.publicName.english} (${selectedDestination.iataCode})`;
    setLocation(selectedDestination.publicName.english);
    setFilter(prev => ({ ...prev, from: selectedDestination.iataCode }));
    setShowFromDropdown(false);
  };

  const handleToDestination = (selectedDestination) => {
    const displayText = `${selectedDestination.publicName.english} (${selectedDestination.iataCode})`;
    setDestination(selectedDestination.publicName.english);
    setFilter(prev => ({ ...prev, to: selectedDestination.iataCode }));
    setShowToDropdown(false);
  };

  const handleShowFlights = () => {
    onSearch(filter); // Parent'taki fonksiyonu çağır
  };

  return (
    <div className="book-your-flights">
      <div className='search-top'>
        <div className='title'>
          <img alt="airplane" className='flight-icon' src={Plane}></img>
          <h2>BOOK YOUR FLIGHT</h2>
        </div>
      </div>
      <div className='search-wrap'>
        <div className='location-wrap'>
          <div className='w100'>
            <label>From</label>
            <input
              className='left-radius'
              type="text"
              value={filter.from}
              onChange={(e) => setFilter(prev => ({ ...prev, from: e.target.value }))}
            />
            {showFromDropdown && (
              <div className="dropdown">
                {filteredFromDestinations.map((dest, index) => (
                  <div key={index} onClick={() => handleFromDestination(dest)}>
                    {`${dest.publicName.english} (${dest.iataCode})`}
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className='w100'>
            <label>To</label>
            <input
              className='right-radius'
              type="text"
              value={filter.to}
              onChange={(e) => setFilter(prev => ({ ...prev, to: e.target.value }))}
            />
            {showToDropdown && (
              <div className="dropdown">
                {filteredToDestinations.map((dest, index) => (
                  <div key={index} onClick={() => handleToDestination(dest)}>
                    {`${dest.publicName.english} (${dest.iataCode})`}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
        {/* Diğer alanlar */}
      </div>
      <button className='show-flight' onClick={handleShowFlights}>Show Flights</button>
    </div>
  );
};

export default SearchFlight;
