import React, { useState } from 'react';
import '../../assets/style/FlightRecord.css';
import Departure from '../../assets/images/departures.png';
import Arrival from '../../assets/images/arrivals.png';
import Plane from '../../assets/images/plane.png';
import FlightService from '../../services/FlightService';
import { calculateDuration, formatDate } from '../../utils/util.js';

const FlightRecord = ({ flight }) => {
    const [detailsVisible, setDetailsVisible] = useState(false);

    const {
        prefixICAO,
        flightName,
        flightNumber,
        scheduleDateTime,
        expectedTimeGateOpen,
        expectedTimeBoarding,
        estimatedLandingTime,
        prefixIATA,
        aircraftRegistration,
        departureCode,
        route: { destinations },
        aircraftType,
        farePrice,
        tripType
    } = flight;

    const bookFlight = async () => {
        try {
            const response = await FlightService.bookFlight({ data: flight });
            console.log(response);
        } catch (error) {
            console.error('Error booking flight:', error);
        }
    };

    const flightDuration = calculateDuration(estimatedLandingTime || expectedTimeBoarding, scheduleDateTime);

    const toggleDetails = () => {
        setDetailsVisible(!detailsVisible);
    };

    return (
        <div className="flight-record">
            <div className="top-section">
                <h4>{departureCode}</h4>
                <h4>-</h4>
                <h4>{destinations[0]}</h4>
            </div>

            <div className="details-section">
                <div>
                    <div className="f g5">
                        <img alt="Departure" className="flight-icon" src={Departure} />
                        <p className="bold-text gray">Departure</p>
                    </div>
                    <p className="bold-text dark-gray">{formatDate(scheduleDateTime)}</p>
                    <p className="bold-text gray">Airport: {departureCode}</p>
                </div>

                <div className="line-wrap">
                    <div className="line"></div>
                </div>

                <div className="airline">
                    <img
                        alt={prefixIATA}
                        className="airline-icon"
                        src={`https://pics.avs.io/200/50/${prefixIATA}.png`}
                        onError={(e) => (e.target.src = '')}
                    />
                    <img alt="Plane" className="flight-icon" src={Plane} />
                    <p className="bold-text">{flightDuration}</p>
                </div>

                <div className="line-wrap">
                    <div className="line"></div>
                </div>

                <div>
                    <div className="f g5">
                        <img alt="Arrival" className="flight-icon" src={Arrival} />
                        <p className="bold-text gray">Arrival</p>
                    </div>
                    <p className="bold-text dark-gray">{estimatedLandingTime ? formatDate(estimatedLandingTime) : 'TBA'}</p>
                    <p className="bold-text gray">Airport: {destinations[0]}</p>
                </div>
            </div>

            <div className="bottom-section">
                <div className="price-info">
                    <p className="price-tag">Price: ${farePrice || '0'}</p>
                    <p className="bold-text gray">{tripType}</p>
                </div>
                <button onClick={bookFlight} className="book-flight">Book Flight</button>
            </div>

            <button onClick={toggleDetails} className="check-details">
                {detailsVisible ? 'Hide Details' : 'Check the details'}
            </button>

            {detailsVisible && (
                <div className="flight-details-section">
                    <div className="flight-details">
                        <h5 className="bold-text">Boarding</h5>
                        <p className="gray bold-text"><strong>Boarding Time: </strong> {expectedTimeBoarding ? new Date(expectedTimeBoarding).toLocaleTimeString() : 'TBA'}</p>
                        <p className="gray bold-text"><strong>Gate Opens: </strong> {expectedTimeGateOpen ? new Date(expectedTimeGateOpen).toLocaleTimeString() : 'TBA'}</p>
                        <p className="gray bold-text"><strong>Landing: </strong> {estimatedLandingTime ? new Date(estimatedLandingTime).toLocaleTimeString() : 'TBA'}</p>
                    </div>

                    <div className="flight-details">
                        <h5 className="bold-text">Aircraft</h5>
                        <p className="gray bold-text"><strong>Registration: </strong> {aircraftRegistration || 'N/A'}</p>
                        <p className="gray bold-text"><strong>Type: </strong> {aircraftType?.iataMain || 'N/A'}</p>
                    </div>

                    <div className="flight-details">
                        <h5 className="bold-text">Flight</h5>
                        <p className="gray bold-text"><strong>Number: </strong> {flightNumber}</p>
                        <p className="gray bold-text"><strong>Name: </strong> {flightName}</p>
                        <p className="gray bold-text"><strong>Type: </strong> {aircraftType?.iataMain || 'N/A'}</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default FlightRecord;
