import axios from 'axios';
const baseURL = process.env.REACT_APP_PROD ? 'https://flight-booking-api-vtk1.onrender.com/' : 'http://localhost:3003/api/';

const apiInstance = axios.create({
    baseURL: baseURL,
    headers: {
        "Content-Type": "application/json",
    },
    withCredentials: true,
});

export default apiInstance
