    <div className="flight-history-page">
      <FilterSection />
      <div className='main-wrap'>
        <SortSection avgFare="$500" sortOptions={sortOptions} onSortSelect={handleSort} />
        <div className="history-list">
          {loading ? (
            <LoadingScreen />
          ) : flightDataList.length === 0 ? (
            <p>No flight history available.</p>
          ) : (
            newFlights.map((flight, index) => (
              <FlightHistoryCard key={index} flight={flight} />
            ))
          )}
        </div>
      </div>

    </div>
  )
}

export default FlightHistory
