import React, { useEffect, useState } from 'react';
import '../../assets/style/SearchFlight.css';
import Plane from '../../assets/images/plane.png';
import FlightService from '../../services/FlightService';

const SearchFlight = ({ onSearch }) => {
  const [filter, setFilter] = useState({
    from: '',
    to: '',
    depart: '',
    arrival: ''
  });
  const [inputToValue, setInputToValue] = useState(''); // Input field value
  const [toDestinations, setToDestination] = useState([]);
  const [filteredToDestinations, setFilteredToDestinations] = useState([]);
  const [showToDropdown, setShowToDropdown] = useState(false);
  
  useEffect(() => {
    const fetchToDestinations = async () => {
      if (inputToValue) { // Only fetch if input has value
        try {
          const flightData = await FlightService.getDestinations({ search: inputToValue });
          setToDestination(flightData);
        } catch (error) {
          console.error('Error fetching to destinations:', error);
        }
      }
    };
  
    fetchToDestinations();
  }, [inputToValue]); // Only trigger when inputToValue changes
  
  useEffect(() => {
    if (inputToValue && toDestinations.length) {
      setFilteredToDestinations(toDestinations);
      setShowToDropdown(true);
    } else {
      setShowToDropdown(false);
    }
  }, [inputToValue, toDestinations]);
  
  const handleToDestination = (selectedDestination) => {
    setFilter(prev => ({ ...prev, to: selectedDestination.iata })); // Set the actual filter value
    setInputToValue(selectedDestination.iata); // Show selected value in the input
    setShowToDropdown(false);
  };
  
  const handleShowFlights = () => {
    onSearch(filter);
  };

  return (
    <div className="book-your-flights">
      <div className='search-top'>
        <div className='title'>
          <img alt="airplane" className='flight-icon' src={Plane}></img>
          <h2>BOOK YOUR FLIGHT</h2>
        </div>

        <div className='trip-type'>
          <button className='left'>Round Trip</button>
          <button className='right'>One Way</button>
        </div>
      </div>
      <div className='search-wrap'>
        <div className='location-wrap'>
          <div className='w100'>
            <label>From</label>
            <input
              className='left-radius'
              type="text"
              value={'AMS'}
              onChange={(e) => setFilter(prev => ({ ...prev, from: e.target.value }))}
              disabled
            />
          </div>
          <div className='w100'>
            <label>To</label>
            <input
              className='right-radius'
              type="text"
              value={inputToValue}
              onChange={(e) => setFilter(prev => ({ ...prev, to: e.target.value }))}
            />
            {showToDropdown && (
              <ul className="dropdown-menu">
                {filteredToDestinations.map((dest, index) => (
                  <li
                    key={index}
                    onClick={() => handleToDestination(dest)}
                    className="dropdown-item"
                  >
                    {`${dest?.publicName?.english} (${dest?.iata || ''})`}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
        <div className='date-wrap'>
          <div className='w100'>
            <label>Depart</label>
            <input
              className='left-radius'
              type="date"
              value={filter.depart}
              onChange={(e) => setFilter(prev => ({ ...prev, depart: e.target.value }))}
            />
          </div>
          <div className='w100'>
            <label>Return</label>
            <input
              className='right-radius'
              type="date"
              value={filter.arrival}
              onChange={(e) => setFilter(prev => ({ ...prev, arrival: e.target.value }))}
            />
          </div>
        </div>
      </div>

      <button className='show-flight' onClick={handleShowFlights}>Show Flights</button>
    </div>
  );
};

export default SearchFlight;
