import React, { useEffect, useState } from 'react';
import FlightService from '../../services/FlightService';
import Arrival from '../../assets/images/arrivals.png';

const SearchFlight = ({ onSearch }) => {
  const [filter, setFilter] = useState({
    from: 'AMS',
    to: '',
    depart: '',
    arrival: '',
    iataCode: '',
    page: 0
  });

  const [toDestinations, setToDestination] = useState([]);
  const [showToDropdown, setShowToDropdown] = useState(false);

  const debounce = (func, delay) => {
    let timeout;
    return (...args) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        func.apply(this, args);
      }, delay);
    };
  };

  const fetchToDestinations = debounce(async (searchTerm) => {
    if (searchTerm) {
      try {
        const data = await FlightService.getDestinations({ search: searchTerm });
        setToDestination(data);
        setShowToDropdown(data.length > 0);
      } catch (error) {
        console.error('Error fetching to destinations:', error);
      }
    } else {
      setToDestination([]);
      setShowToDropdown(false);
    }
  }, 1000);

  const handleInputChange = (e) => {
    const newValue = e.target.value;
    setFilter(prev => ({ ...prev, to: newValue }));
    fetchToDestinations(newValue);
  };

  const handleToDestination = (destination) => {
    setFilter(prev => ({ ...prev, to: destination.iata }));
    setShowToDropdown(false);
  };

  const handleShowFlights = () => {
    onSearch(filter);
    setToDestination([]);
    setShowToDropdown(false);
  };

  return (
    <div className='w100'>
      <label>To</label>
      <div className="input-container right-radius">
        <img alt='arrival' src={Arrival} />
        <input
          className="right-radius"
          type="text"
          value={filter.to}
          onChange={handleInputChange} // Burada kullan覺l覺yor
        />
      </div>
      {showToDropdown && (
        <div className="dropdown">
          {toDestinations.map(destination => (
            <div key={destination.iata} onClick={() => handleToDestination(destination)}>
              {destination.name}
            </div>
          ))}
        </div>
      )}
      <button onClick={handleShowFlights}>Show Flights</button>
    </div>
  );
};

export default SearchFlight;
