import React, { useEffect, useState, useCallback } from 'react';
import '../assets/style/FlightHistoryPage.css';
import FilterSection from '../components/flightHistory/Filters.jsx';
import LoadingScreen from '../components/shared/Loading';
import SortSection from '../components/flightHistory/SortSection.jsx';
import FlightHistoryCard from '../components/flightHistory/FightHistoryCard';
import FlightService from '../services/FlightService';

const sortOptions = [
  { value: 'farePrice', label: 'Lowest Price', order: 1 },
  { value: 'farePrice', label: 'Highest Price', order: -1 },
  { value: 'scheduleDate', label: 'Earliest Flight', order: 1 },
  { value: 'scheduleDate', label: 'Latest Flight', order: -1 },
  { value: 'flightName', label: 'Flight Name', order: 1 },
  { value: 'airlineCode', label: 'Airline', order: 1 },
];

const FlightHistory = () => {
  const [newFlights, setFlights] = useState([]);
  const [averageFare, setAverageFare] = useState(0);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({ sortBy: 'farePrice', order: 1 });

  const getFlightBookings = useCallback(async () => {
    try {
      setLoading(true);
      const flightData = await FlightService.getFlights({
        params: {
          sortBy: filters.sortBy, order: filters.order, page: 1, limit: 10
        }
      });
      setFlights(flightData);
      console.log(flightData)
    } catch (error) {
      console.error('Error fetching flights:', error);
    }
    finally {
      setLoading(false);
    }
  }, [filters]);

  const getAveragePrice = useCallback(async () => {

    try {
      const price = await FlightService.getAverageFarePrice();
      setAverageFare(price);
    } catch (error) {
      console.error('Error fetching fare price:', error);
      setAverageFare(0);
    }
  }, []);

  useEffect(() => {
    getFlightBookings();
    getAveragePrice();
  }, [getFlightBookings, getAveragePrice]);

  const handleSort = (sort) => {
    setFilters({ order: sort.order, sortBy: sort.value })
  };

  if (loading) return <LoadingScreen />;

  return (
    <div className="flight-history-page">
      <FilterSection filters={filters} setFilters={setFilters} />
      <SortSection options={sortOptions} onSortSelect={handleSort} />
      {newFlights?.length > 0 ? (
        newFlights.map((flight) => (
          <FlightHistoryCard key={flight.id} flight={flight} />
        ))
      ) : (
        <p>No flights available</p>
      )}
      <p>Average Fare Price: ${averageFare}</p>
    </div>
  );
}

export default FlightHistory
