import React from 'react';
import '../../assets/style/FlightHistoryCard.css';

const FlightHistoryCard = ({ flight }) => {

    const {
        prefixICAO: airlineName,
        flightName: flightCode,
        scheduleDateTime,
        estimatedLandingTime,
        prefixIATA,
        route: { destinations }
    } = flight;

    const departureTime = new Date(scheduleDateTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    const arrivalTime = new Date(estimatedLandingTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    const [departureCode, arrivalCode] = destinations;

    const totalSlots = 5;
    const filledPackages = flight.flightPackages?.length || 0;  // If flightPackages exists, use length, otherwise 0
    const emptySlots = totalSlots - filledPackages;

    const emptyPackages = Array(emptySlots).fill({ packageName: '', price: '' });
    const allPackages = [...flight.flightPackages || [], ...emptyPackages]; // flightPackages might be undefined, use fallback

    return (
        <div className="flight-history-card">
            <div className="history-left-section">
                <div className='airline-detail'>
                    <div className='logo-wrap'>
                        <img src={`https://pics.avs.io/200/50/${prefixIATA}.png`} alt={airlineName} className="airline-logo" />
                    </div>
                    <div className="airline-info">
                        <div className='time-info'>
                            <p>{departureTime}</p>
                            <p>&nbsp;-&nbsp;</p>
                            <p>{arrivalTime}</p>
                        </div>
                        <div>
                            <div className="airline-sub-info">
                                <div className="gray-bold">{airlineName}</div>
                                <button className="flight-details-btn">Flight Details
                                    <span className='dropdown-arrow'></span>
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="middle-section">
                <div className="stop-info">
                    <div className="gray-bold">{flight.stopCount}</div>
                    <div className="gray-light-bold">{flight.flightDuration}</div>
                </div>
                <div className="location-info">
                    <div className="airport-codes">
                        <span className='gray-bold'>{departureCode}</span>
                        <span className='gray-bold'>to</span>
                        <span className='gray-bold'>{arrivalCode}</span>
                    </div>
                    <div className="gray-light-bold">{flightCode}</div>
                </div>
            </div>

            <div className="history-right-section">
                {allPackages.map((pkg, index) => (
                    <div key={index} className={`flight-package ${pkg.packageName ? '' : 'empty'}`}>
                        {pkg.packageName ? (
                            <>
                                <div className="price">{pkg.price}</div>
                                <div className="package-name">{pkg.packageName}</div>
                            </>
                        ) : null}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FlightHistoryCard;
